import React from 'react';
//import { dummyPost } from '../dummy.js';
import styled from 'styled-components';

export const Postform = styled.div`
display: inline-block;
width: 230px;
height: 300px;
border: solid rgb(238, 238, 238) 0.1rem;
margin: 0.3rem;
background-color: white;
`
export const Postforms = styled.div`
display: flex;
width: 100vw;
height: 30px;
border: 1px solid black;
background-color: skyblue;
`
export const Postcontent = styled.div`
float: right;
margin-right: 1rem;
font-weight: 700;
white-space: nowrap;
overflow:hidden;
text-overflow: ellipsis;
`
export const Postimg = styled.img`
width: 210px;
height: 210px;
margin: 0.5rem;
border: 0.5px solid rgb(238, 238, 238);
object-fit: cover;
`
export const Posttitle = styled.div`
float: left;
margin-left: 1rem;
font-weight: 400;
`
export const Postlikes = styled.div`
float: right;
margin-right: 1rem;
font-weight: 700;
`
export default function Boardpostform({posts}){
    return(
        <>
            <Postform key={posts.id}>
            <Postimg src={posts.image} alt={posts.title} />
            <Postforms>
            <Posttitle>{posts.title}</Posttitle>
            <Postcontent>{posts.contents}</Postcontent>
            <Postlikes>{posts.total_likes}</Postlikes>
            </Postforms>
            </Postform>
        </>
    )
}
