//   const KAKAO_AUTH_URL=`https://kauth.kakao.com/oauth/authorize?client_id=${REST_API_KEY}&redirect_uri=${REDIRECT_URI}&response_type=code`;
//   const handlekakaoLogin=()=>{
//   window.location.href =KAKAO_AUTH_URL
//   document.getElementById('login')
// }  
const responseKaKao = (res) => {

  console.log(res)
  fetch('http://localhost:8080/users/kakaologin', {
    //백엔드에서 원하는 형태의 endpoint로 입력해서 fetch한다. 
    method: 'POST',
    headers: {
      Authorization: res.response.access_token,
      //받아오는 response객체의 access_token을 통해 유저 정보를 authorize한다. 

    },
  })
    .then((res) => res.json())
    .then((res) => localStorage.setItem('token', res.token),
    console.log(res),
          //백엔드에서 요구하는 key 값(token)으로 저장해서 localStorage에 저장한다.
          //여기서 중요한것은 처음에 console.log(res)해서 들어오는 
          //access_token 값을 백엔드에 전달해줘서 백엔드에 저장 해두는 
          //절차가 있으므로 까먹지 말 것! 
          alert('로그인 성공하였습니다',res.token));
};

  const [isOpen, setIsOpen] = useState(true);
  const [signUpModalOn, setSignUpModalOn] = useState(false);

  const openModalHandler = () => { 
    setIsOpen(!isOpen);
  };

  const navigate= useNavigate();
  const [loginInfo, setLoginInfo] = useState({
    email: '',
    password: ''
  });
  const [errorMessage, setErrorMessage] = useState('');
  const handleInputValue = (key) => (e) => {
    setLoginInfo({ ...loginInfo, [key]: e.target.value });
  };
  const handleLogin = () => {

    if(loginInfo.email !=='' && loginInfo.password !== ''){
      axios.post('http://localhost:8080/users/login',{
        email: loginInfo.email,
        password: loginInfo.password
      }).then( handleResponseSuccess )
      navigate('/')
      openModalHandler();
    }else{
      setErrorMessage('이메일과 비밀번호를 입력하세요')
    }
  };


  return (

    <div>

          {isOpen ? <ModalBackdrop onClick={openModalHandler}>
                    <ModalView onClick={(event) => {event.stopPropagation()}}>  
        <center>
        <h1>로그인 하기</h1>
        <br /><br />
        <form onSubmit={(e) => e.preventDefault()}>
          <div>
            <div>이메일 입력</div>
            <Input type='email' onChange={handleInputValue('email')} />
          </div>
          <br />
          <div>
            <div>비밀번호 입력</div>
            <Input
              type='password'
              onChange={handleInputValue('password')}
            />
          </div>
          <br /><br /><br /><br /><br /><br /><br /><br />
          {/* <KaKaoBtn onClick={handlekakaoLogin}>카카오 로그인</KaKaoBtn> */}
          {/* <button  onClick={() => {handlekakao()}} href="javascript:void(0)" >
          <span>카카오 로그인</span>
          </button>




   <button onclick={handlekakaologout} href="javascript:void(0)">

            <span>카카오 로그아웃</span>

    </button> */}
    <KaKaoBtn
    //styled component 통해 style을 입혀 줄 예정 
                jsKey={'b30b4663aa8893c9e6cfb686d083f99b'}
    //카카오에서 할당받은 jsKey를 입력
                buttonText='카카오 계정으로 로그인'
    //로그인 버튼의 text를 입력
    onSuccess={responseKaKao}
    //성공했을때 불러올 함수로서 fetch해서 localStorage에 저장할 함수를 여기로 저장 
                getProfile={true}
              />
            <br /><br />
          <ModalBtn  onClick={handleLogin}>
            로그인
          </ModalBtn>
          <br /><br />
          <div>
          <ModalBtn  className="close-btn" onClick={() => setSignUpModalOn(true)} >
            회원가입
          </ModalBtn>
          </div>
          <div className='alert-box'>{errorMessage}</div>

          <Signup show={signUpModalOn} onHide={() => setSignUpModalOn(false)} />

        </form>
      </center>         
                    </ModalView>
                  </ModalBackdrop> : null} 

    </div>
  );
};
