import React,{ useState } from "react";
import { useNavigate } from "react-router";
import styled from 'styled-components';
import axios from 'axios';
import Boardpostform from './Boardpostform';
import { dummyPost } from '../dummy.js';


export const Allpage = styled.div`
width : 99vw;
height : 90vh;
margin: 0 auto;
`
export const Options = styled.div`
width : 99vw;
height : 4vh;
background-color : green;
`
export const ViewBoard =styled.div`
width : 99vw;
height : 90vh;
overflow : auto;
background-color : skyblue;
`
export const Vboard = styled.div`
display: table;
margin: 0 auto;
width: 99%;
height: calc(100vh - 4rem);
`
export const Vboards = styled.div`
background-color: #bc9eff0e;
padding: 5rem;
width: 99%;
min-height: 90vh;
height: 90%;
`
export const Postbutton = styled.button`
float: right;
`

const OPTIONS = [
    { id:1, value: "tags", name: "태그 선택" },
    { id:2, value: "mountain", name: "산" },
    { id:3, value: "river", name: "강" },
    { id:4, value: "sea", name: "바다" },
    { id:5, value: "valley", name: "계곡" },
]; 

const SelectBox = (props) => {
    const [tags, setTags] = useState('태그 선택')
    const handleTags = (e) => {
        console.log(e)
    }
    return (
        <select>
            {props.options.map((option) => (
                <option
                    key={option.id}
                    value={option.value}
                    onClick={() =>{handleTags(option)}}
                >
                    {option.name}
                </option>
            ))}
        </select>
    );
};

export default function Board(props){
console.log(props.postsinfo)
const navigate = useNavigate();
return(
<Allpage>
    <Options>
        태그선택  게시글 나열 방식  <Postbutton onClick={() => {navigate('/create_post');}}>글쓰기</Postbutton>
    </Options>
    <SelectBox options={OPTIONS}/>
    <ViewBoard>
    <Vboard>
      <Vboards>
      {props.postsinfo&&props.postsinfo.map((posts)=>
            <Boardpostform 
            posts={posts}
            key={posts.id}
            />)}
      </Vboards>
    </Vboard>
    </ViewBoard>
</Allpage>


)
}

