  }

  return (
    <div>
      <center>
      <h2>게시글 작성</h2>
      </center>
      <>
        <div>제목</div>
        <TitleInput  type="text" placeholder='제목을 입력해주세요' />
      </>
      <div>태그선택</div>
      <Dropdownbtn id="dropdown-item-button" title={tagName} >
      {props.tags?.map(tags => {
        return <Dropdown.Item as="button" onChange={handleTags} key={tags.id}>{tags.name}</Dropdown.Item>
      })}
    </Dropdownbtn>
    <input 
      type="file" 
      id="real-input" 
      className="image_inputType_file" 
      accept="img/*" 
      required multiple 
      />
<button className="browse-btn">사진업로드</button>
