import React,{ useState, useEffect } from "react";
import { Routes, Route, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios';
import Boardpostform from './Boardpostform';
import Post from './Post';


export const Allpage = styled.div`
width : 99vw;
height : 90vh;
margin: 0 auto;
`
export const Options = styled.div`
width : 99vw;
height : 4vh;
background-color : green;
`
export const Boardbutton = styled.button`
margin-right : 25px
`
export const ViewBoard =styled.div`
width : 99vw;
height : 90vh;
overflow : auto;
background-color : skyblue;
`
export const Vboard = styled.div`
display: table;
margin: 0 auto;
width: 99%;
height: calc(100vh - 4rem);
`
export const Vboards = styled.div`
background-color: #bc9eff0e;
padding: 5rem;
width: 99%;
min-height: 90vh;
height: 90%;
`
export const Postbutton = styled.button`
float: right;
`

export default function Board(props){


const navigate = useNavigate();
  const [tests,settests]=useState({})
  console.log(tests)

  const [postsinfo, setPostsinfo]=useState()
  console.log(postsinfo)
  const isPosts =() =>{
    axios.get('http://localhost:8080/posts').then((res)=>{ 
    const test = res.data.data    
    setPostsinfo(test)
      }).catch(error =>{
        console.log(error)
      })
  }
  useEffect(() => {
    isPosts();
  }, []);

  const handlemovepost=()=>{
    props.PPinfo(tests)
    navigate('/post')
  }
return(

<Allpage>
    <Options>
    <Post tests={tests} />
        태그선택  게시글 나열 방식  <Postbutton onClick={() => {navigate('/create_post');}}>글쓰기</Postbutton>
    </Options>
    <ViewBoard>
    <Vboard>
      <Vboards>
      {postsinfo&&postsinfo.map(posts=>
      {
          return(
            <Boardbutton onClick={() => {settests(posts);handlemovepost()}} >
            <Boardpostform 
            posts={posts}
            key={posts.id}

            />

            </Boardbutton>
          )
           } )}

      </Vboards>
    </Vboard>
    </ViewBoard>
</Allpage>


)
}
