import React, { useState } from "react";
import MapContainer from "./Kakao_Map";

const SearchPlace = ({setCoordinate, setpostInfo, coordinate}) => {
  const [inputText, setInputText] = useState("");
  const [place, setPlace] = useState("");

  const onChange = (e) => {
    setInputText(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setPlace(inputText);
    setInputText("");
    console.log(coordinate)
    setpostInfo((e) => {
      return {...e, longitude: coordinate.x}
    })
  };

  return (
    <>
      <MapContainer searchPlace={place} setCoordinate={setCoordinate} />
      <form className="inputForm" onSubmit={handleSubmit}>
        <input
          placeholder="여행지역을 검색해주세요"
          onChange={onChange}
          value={inputText}
        />
        <button type="submit">검색</button>
      </form>
    </>
  );
};

export default SearchPlace;
