import React,{useState} from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router';
import { Button, Form } from "react-bootstrap";
import styled from 'styled-components';

const Errmessage = styled(Form.Text)`
color: red;
`
export const Editstyle=styled.div`
margin: auto;
width:60%;
height: 100%;
margin-top: 5%;
background: #f5cd79;
border: 1px solid black;
border-radius: 20px;
text-align: center;
`
export const Input = styled.input`
width:80%;
height:60%;
min-height:40px;
`
export const Emails = styled.div`
width:80%;
height:60%;
min-height:40px;
background-color: white;
border:1px solid black;
`
export default function EditProfile(props) {
  const [editInfo, setEditInfo] = useState({
    name: '',
    email: props.userinfo.email,
    password: '',
    newpassword:'',
    samepassword:''
  });
  console.log(props.userinfo)
  console.log(editInfo)
  const handleInputValue = (key) => (e) => {
    setEditInfo({ ...editInfo, [key]: e.target.value });
  };
  const navigate= useNavigate();
  const [errUserName, setErrUserName] = useState('');
  const [errPassword, setErrPassword] = useState('');
  const [errPasswordChecked, setErrPasswordChecked] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const handleEdit = () => {

    if(editInfo.name !=='' && editInfo.password !== '' && editInfo.newpassword !== '' && editInfo.samepassword !==''){
      axios.patch(`http://localhost:8080/users/${props.userinfo.id}`,{
        name: editInfo.name,
        password: editInfo.newpassword,
      }).then(props.userinfo.name = editInfo.name,props.userinfo.password = editInfo.password)   
    }
    navigate('/mypage')
  };
  //console.log(editInfo)
  return (

<Form>
<Form.Group className="mb-3">
    <Form.Label>이름</Form.Label>
    <Form.Control
      placeholder="이름을 입력해주세요."
      onChange={e => handleInputValue('userName', e)} 
    />
    <Errmessage className="text_muted">
      {errUserName}
    </Errmessage>
  </Form.Group>

  <Form.Group className="mb-3">
    <Form.Label>이메일</Form.Label>
    <Form.Control 
      type="email"
      placeholder="이메일을 입력해주세요."
      onChange={e => handleInputValue('email',e)} 
    />
    <Errmessage className="text_muted">
          </Errmessage>
  </Form.Group>

  <Form.Group className="mb-3">
    <Form.Label>비밀번호</Form.Label>
    <Form.Control
      type="password"
      placeholder="비밀번호를 입력해주세요."
      onChange={e => handleInputValue('password', e)}  
    />
    <Errmessage className="text_muted">
      {errPassword}
    </Errmessage>
  </Form.Group>

  <Form.Group className="mb-3">
    <Form.Label>비밀번호 확인</Form.Label>
    <Form.Control
      type="password"
      placeholder="비밀번호를 입력해주세요."
      onChange={e => handleInputValue('passwordChecked', e)} 
    />
    <Errmessage className="text_muted">
      {errPasswordChecked}
    </Errmessage>
  </Form.Group>
  <center>
  <Button 
    variant='primary'
    type='button' 
    className='SingupBtn' 
    /*onClick={handleSignup}*/
  >
    회원가입
  </Button>
  <div>
  <Errmessage className="text_muted">
      {errorMessage}
    </Errmessage>
  </div>
  </center>
</Form>
  )
}
