import axios from 'axios';
import styled from 'styled-components';
import React, { useState, } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaAngleDown } from "react-icons/fa";
axios.defaults.withCredentials = true;

const TitleInput = styled.input`
  margin: auto;
  width : 40vw;
  height : 3vh;
  `
  const DropDownContainer = styled.div`
  .container {

    width: 170px;
    box-shadow: 0 4px 5px 0 #00000026;
    position: relative;
    z-index: 10;
  }
  #dropdown {
    left: 0;
    visibility: hidden;
    position: absolute;
  }
  .dropdownLabel {
    display: flex;
    justify-content: space-between;
    padding: 12px;
  }
  .dropdownLabel:hover {
    cursor: pointer;
  }
  .content {
    display: none;
    position: absolute;
    width: 100%;
    left: 0;
    background: white;
    box-shadow: 0 4px 5px 0 #00000026;
  }

  #dropdown:checked + label + div.content {
    display: block;
    border-top: 1px solid #00000026;
  }
  .caretIcon {
    transition: transform 250ms ease-out;
  }
  #dropdown:checked + label > .caretIcon {
    transform: rotate(-180deg);
  }

  .content ul {
    list-style-type: none;
    padding: 15px;
    margin: 0;
  }
  .content ul li {
    margin: 0.8rem 0;
  }
  `

export default function Create_post(props) {
  const [postInfo, setpostInfo] = useState();
  const [userinfo, setUserinfo] = useState(props);
  const userEmail = props.userinfo.email
  console.log(props.userinfo.email)
  console.log(props.tags)


  const Tags = props.tags.map((tags) => 
    <li>{tags.name}</li>
  )

  return (
    <div>
      <>
        <div>제목</div>
        <TitleInput block type="text" placeholder='제목을 입력해주세요' />
      </>
      <div>태그</div>
        <DropDownContainer>
        <div className="container">
          <input id="dropdown" type="checkbox" />
          <label className="dropdownLabel" for="dropdown">
            <div>Tags</div>
          <FaAngleDown className="caretIcon" />
          </label>
        <div className="content">
          <ul>
            {Tags}
          </ul>
        </div>
      </div>
      </DropDownContainer>

    </div>
  )
}
