import React, { useState, useEffect, useRef, Component } from 'react';
import { Button } from 'react-bootstrap'
import axios from 'axios';
import { useNavigate } from 'react-router-dom'
import styled from 'styled-components';
import Signup from './Signup/Signup';
import { REST_API_KEY, REDIRECT_URI} from './KakaoLogindata'

axios.defaults.withCredentials = true;
export const KaKaoBtn = styled(Button)`
padding: 0;
  width: 300px;
  height: 45px;
  line-height: 44px;
  color: #783c00;
  background-color: #ffeb00;
  border: 1px solid transparent;
  border-radius: 3px;
  font-size: 14px;
  font-weight: bold;
  text-align: center;
  cursor: pointer;
  &:hover {
    box-shadow: 0 0px 15px 0 rgba(0, 0, 0, 0.2);
`
export const ModalContainer = styled.div`
  display : flex;
  justify-content: center;
  align-items: center;
  position: relative;
  margin: 5em;
`;
export const Input = styled.input`
width: 80%;
height:150%;
min-height: 40px;
`
export const ModalBackdrop = styled.div`
position: fixed;
display: flex;
justify-content: center;
align-items: center;
width: 100vw;
height: 100vh;
background-color : none;
`;

export const ModalBtn = styled.button`
  background-color: #4000c7;
  text-decoration: none;
  border: none;
  padding: 20px;
  color: white;
  border-radius: 30px;
  cursor: pointer;
`;

export const ModalView = styled.div`
background-color: white;
width: 40%;
min-width: 300px;
max-width: 600px;
height: 80%;
overflow-y: auto;
border:solid;
position: fixed;
left: 50%;
top: 50%;
padding: 5px;
transform: translate(-50%, -50%);
z-index: 1011;
`;
const { Kakao } = window;
export default function Login ({ handleResponseSuccess }) {
  Kakao.init('b30b4663aa8893c9e6cfb686d083f99b'); //발급받은 키 중 javascript키를 사용해준다.
      console.log(Kakao.isInitialized()); // sdk초기화여부판단
      //카카오로그인
      function kakaoLogin() {
          Kakao.Auth.login({
            success: function (response) {
              Kakao.API.request({
                url: '/v2/user/me',
                success: function (response) {
                  console.log(response)
                },
                fail: function (error) {
                  console.log(error)
                },
              })
            },
            fail: function (error) {
              console.log(error)
            },
          })
        }
      //카카오로그아웃  
      function kakaoLogout() {
          if (Kakao.Auth.getAccessToken()) {
            Kakao.API.request({
              url: '/v1/user/unlink',
              success: function (response) {
                console.log(response)
              },
              fail: function (error) {
                console.log(error)
              },
            })
            Kakao.Auth.setAccessToken(undefined)
          }
        }
  const KAKAO_AUTH_URL=`https://kauth.kakao.com/oauth/authorize?client_id=${REST_API_KEY}&redirect_uri=${REDIRECT_URI}&response_type=code`;
  const handlekakaoLogin=()=>{
  window.location.href =KAKAO_AUTH_URL
  document.getElementById('login')
}  

  const [isOpen, setIsOpen] = useState(true);
  const [signUpModalOn, setSignUpModalOn] = useState(false);

  const openModalHandler = () => { 
    setIsOpen(!isOpen);
  };

  const navigate= useNavigate();
  const [loginInfo, setLoginInfo] = useState({
    email: '',
    password: ''
  });
  const [errorMessage, setErrorMessage] = useState('');
  const handleInputValue = (key) => (e) => {
    setLoginInfo({ ...loginInfo, [key]: e.target.value });
  };
  const handleLogin = () => {

    if(loginInfo.email !=='' && loginInfo.password !== ''){
      axios.post('http://localhost:8080/users/login',{
        email: loginInfo.email,
        password: loginInfo.password
      }).then( handleResponseSuccess )
      navigate('/')
      openModalHandler();
    }else{
      setErrorMessage('이메일과 비밀번호를 입력하세요')
    }
  };


  return (

    <div>

          {isOpen ? <ModalBackdrop onClick={openModalHandler}>
                    <ModalView onClick={(event) => {event.stopPropagation()}}>  
        <center>
        <h1>로그인 하기</h1>
        <br /><br />
        <form onSubmit={(e) => e.preventDefault()}>
          <div>
            <div>이메일 입력</div>
            <Input type='email' onChange={handleInputValue('email')} />
          </div>
          <br />
          <div>
            <div>비밀번호 입력</div>
            <Input
              type='password'
              onChange={handleInputValue('password')}
            />
          </div>
          <br /><br /><br /><br /><br /><br /><br /><br />
          {/* <KaKaoBtn onClick={handlekakaoLogin}>카카오 로그인</KaKaoBtn> */}
          {/* <button  onclick={kakaoLogin} href="javascript:void(0)" >
          <span>카카오 로그인</span>
          </button>




   <button onclick={kakaoLogout} href="javascript:void(0)">

            <span>카카오 로그아웃</span>

    </button> */}

            <br /><br />
          <ModalBtn  onClick={handleLogin}>
            로그인
          </ModalBtn>
          <br /><br />
          <div>
          <ModalBtn  className="close-btn" onClick={() => setSignUpModalOn(true)} >
            회원가입
          </ModalBtn>
          </div>
          <div className='alert-box'>{errorMessage}</div>

          <Signup show={signUpModalOn} onHide={() => setSignUpModalOn(false)} />

        </form>
      </center>         
                    </ModalView>
                  </ModalBackdrop> : null} 

    </div>
  );
};
