import axios from 'axios';
import {Dropdown, DropdownButton} from 'react-bootstrap';
import styled from 'styled-components';
import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import KakaoMap from '../components/SearchPlace'

axios.defaults.withCredentials = true;

const TitleInput = styled.input`
  margin: auto;
  width : 40vw;
  height : 3vh;
  `
const Dropdownbtn = styled(DropdownButton)`
  #dropdown-item-button{
    width: 9vw;
    height: 4vh;
    line-height: 1rem;
    margin: 20px;
    fo
  }

`

const PreviewDiv = styled.div`
  margin-left: 10px;
  border: 5px solid rgb(122, 238, 72);
  border-radius: 20px;
  min-width: 300px;
  min-height: 300px;
  width: 22%;
  height: 20%;
  .previewImg {
    width: 300px;
    height: 300px;
  }
` 

export default function Create_post(props) {
  const [postInfo, setpostInfo] = useState({
    user_id: '',
    contents: '',
    title: '',
    tag_id: '',
    total_likes: 0,
    image: '',
    longitude: '',
    latitude: '',
  });
  const [userinfo, setUserinfo] = useState(props);
  const [tagName, setTagName] = useState('태그');
  const [imageSrc, setImageSrc] = useState('');
  const userEmail = props.userinfo.email
  const inputRef = useRef();
  console.log(props.userinfo.email)
  console.log(props.tags)
  console.log('렌더링...')

  const handleImage = () => {
    inputRef.current.click()
  }

  const handleTags = (e) => {
    setTagName(e.name)
    console.log(e)
  }

  const preview = (fileBlob) => {    
    const reader = new FileReader();
    reader.readAsDataURL(fileBlob);
    return new Promise((resolve) => {
      reader.onload = () => {
        setImageSrc(reader.result);
        resolve();
      };
    });
  };

  const handlePostInfo = () => {

  }

  return (
    <div>
      <center>
      <h2>게시글 작성</h2>
      </center>
      <>
        <div>제목</div>
        <TitleInput  type="text" placeholder='제목을 입력해주세요' onChange={handlePostInfo('title')} />
      </>
      <div>태그선택</div>
      <Dropdownbtn id="dropdown-item-button" title={tagName} >
      {props.tags?.map(tags => {
        return <Dropdown.Item 
        as="button" 
        key={tags.id}  
        onClick={() =>{
          handleTags(tags)
        }}
        >
        {tags.name}
        </Dropdown.Item>
      })}
    </Dropdownbtn>
    <div>이미지</div>
    <PreviewDiv>
      <div className="preview" >
        {imageSrc && <img src={imageSrc} alt="preview-img" className="previewImg" />}
      </div>
    </PreviewDiv>
    <input
      ref={inputRef}
      type="file" 
      id="real-input" 
      className="image_inputType_file" 
      style={{ display: "none" }}
      accept="image/*" 
      onChange={(e) => {
        preview(e.target.files[0]);
      }}
      required multiple 
      />
    <button className="browse-btn" onClick={handleImage}>
      사진업로드
    </button>
    <div>글 작성</div>
      <input type="text" placeholder='글을 작성해주세요' />
    <div>지도</div>
      <KakaoMap />
      <button>작성완료</button>

    </div>
  )
}
